INSERT INTO `knowledge_content` VALUES (11, '<p style=\"text-align: left;\"><strong><span style=\"font-size: 14pt;\">1.定义</span></strong></p>\n<ul style=\"text-align: left;\">\n<li style=\"text-align: left;\">jQuery是javaScript的框架，是一套工具库，简化了javaScript的功能实现</li>\n<li style=\"text-align: left;\">它将很多功能封装成了函数来进行使用;</li>\n<li style=\"text-align: left;\">它可以兼容不同的浏览器</li>\n</ul>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.JS代码与JQ代码的区别</strong></span></p>\n<ol style=\"text-align: left;\">\n<li>JS是一个DOM对象，而JQuery是一个JQ对象[DOM对象,DOM对象,DOM对象...]</li>\n<li>js获取页面上的一个div：document.getElementById(\'d1\');</li>\n<li>JQuery获取页面上的一个div：$(\'#d1\'); -------直接调用函数实现</li>\n<li>jQuery对象与DOM对象可以相互转换</li>\n</ol>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>3. jQuery库功能：</strong></span></p>\n<ul>\n<li style=\"text-align: left;\">HTML元素选取</li>\n<li style=\"text-align: left;\">HTML元素操作</li>\n<li style=\"text-align: left;\">CSS操作</li>\n<li style=\"text-align: left;\">HTML事件函数</li>\n<li style=\"text-align: left;\">javaScript特效和动画</li>\n<li style=\"text-align: left;\">HTML DOM遍历和修改</li>\n<li style=\"text-align: left;\">AJAX&nbsp;&nbsp;</li>\n</ul>', 18);
INSERT INTO `knowledge_content` VALUES (12, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1. 基础用法：</strong></span></p>\n<p style=\"text-align: left;\">选择器类型&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;CSS中的表示&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;JQuery中的表示</p>\n<p style=\"text-align: left;\">元素选择器:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 元素名&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'CSS中的表示\')</p>\n<p style=\"text-align: left;\">类选择器：&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;.类名&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;~~~~~</p>\n<p style=\"text-align: left;\">id选择器：&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;#id&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ~~~~~</p>\n<p style=\"text-align: left;\">&nbsp;(后代)派生选择器：&nbsp; &nbsp; &nbsp; &nbsp;元素&nbsp; 后代元素&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;~~~~~</p>\n<p style=\"text-align: left;\">儿子选择器：&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;父元素&gt;子元素&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;~~~~~</p>\n<p style=\"text-align: left;\">&nbsp;毗邻选择器：&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 元素+元素(第一个弟弟元素)&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ~~~~~</p>\n<p style=\"text-align: left;\">&nbsp;弟弟选择器：&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 元素~元素(所有弟弟)&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;~~~~~</p>\n<p style=\"text-align: left;\">&nbsp;属性选择器：&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; [属性名] 、[属性名=值] 、标签名[属性名=值]&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;~~~~~</p>\n<p style=\"text-align: left;\">全选择器：&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'*\')</p>\n<p style=\"text-align: left;\">多选择器共用&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'选择器1，选择器2...\')</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.进阶用法</strong></span></p>\n<ul style=\"text-align: left;\">\n<li>后代选择方法 find()；</li>\n</ul>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').find(\"p\");&nbsp; //表示选择到div后代中的p标签</p>\n<ul style=\"text-align: left;\">\n<li>子代选择器 children()：</li>\n</ul>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').children(\"p\"); //表示获取div中所有的p标签子元素（儿子、孙子、子孙后代）</p>\n<ul style=\"text-align: left;\">\n<li>父选择器 parent()：</li>\n</ul>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'li\').parent();&nbsp; &nbsp;//表示获取li元素的父元素</p>\n<ul style=\"text-align: left;\">\n<li>后一个兄弟节点 next()：</li>\n</ul>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'li\').next();&nbsp; &nbsp;//表示获取li之后的下一个兄弟节点</p>\n<ul style=\"text-align: left;\">\n<li>前一个兄弟节点 prev()：</li>\n</ul>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'li\').prev();&nbsp; &nbsp;//表示获取li之前的前一个兄弟节点</p>', 19);
INSERT INTO `knowledge_content` VALUES (13, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>一、简单过滤选择器</strong></span></p>\n<p style=\"text-align: left;\">:first&nbsp; //选出匹配到的标签中的第一个元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;$(\'div&gt;input:first\');&nbsp; &nbsp;//div下所有input子标签中的第一个</p>\n<p style=\"text-align: left;\">:last&nbsp; &nbsp;//选出匹配到的标签中的最后一个元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;$(\'div&gt;input:last\');&nbsp; &nbsp;//div下所有input子标签中的最后一个</p>\n<p style=\"text-align: left;\">:eq(index)&nbsp; //选出对应索引值的元素&nbsp; &nbsp;*index从0开始</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp;$(\'div span+div&gt;input:eq(1)\');&nbsp; //div下所有span后代的后续紧邻的div下的所有input子标签中的第一个</p>\n<p style=\"text-align: left;\">:gt(index)&nbsp; //选出索引值大于index的元素&nbsp; &nbsp;*index从0开始</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; $(\'div&gt;input:gt(1)\');&nbsp; &nbsp; //div下所有的input子标签中索引大于1的</p>\n<p style=\"text-align: left;\">:lt(index)&nbsp; //选出索引值小于index的元素&nbsp; &nbsp;*index从0开始</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'div&gt;input:lt(1)\');&nbsp; &nbsp; //div下所有的input子标签中索引小于1的</p>\n<p style=\"text-align: left;\">:odd&nbsp; &nbsp;//选出索引值为奇数的元素&nbsp; *从0开始</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'div&gt;input:odd\');&nbsp; &nbsp; //div下所有的input子标签中索引为1，3，5...的</p>\n<p style=\"text-align: left;\">:even&nbsp; &nbsp;//选出索引值为偶数的元素&nbsp; *从0开始</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'div&gt;input:odd\');&nbsp; &nbsp; //div下所有的input子标签中索引为2，4，6...的</p>\n<p style=\"text-align: left;\">:not(selector)&nbsp; &nbsp; //选出selector不能匹配到的元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'div&gt;input:not(.a)\');&nbsp; &nbsp;//div下所有的input子标签中样式类名不是a的</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'div&gt;input(.a)\');&nbsp; &nbsp;//div下所有的input子标签中样式类名是a的&nbsp; ？？？</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'#abc&gt;li:not(#a)\');&nbsp; &nbsp;//id为abc的标签下的li子标签中id不是a的</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'#abc&gt;li:not(:eq(2))\');&nbsp; &nbsp;//id为abc的标签下的li子标签中索引不是2的</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>二、内容过滤器(了解)</strong></span></p>\n<p style=\"text-align: left;\">:empty&nbsp; 没有内容的标签（标签体中的文字或子标签）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'p:empty\');</p>\n<p style=\"text-align: left;\">:parent&nbsp; 有内容的标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'p:parent\');</p>\n<p style=\"text-align: left;\">:has(selector)&nbsp; &nbsp;拥有某种后代的标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'div:has(p)\');</p>\n<p style=\"text-align: left;\">:contains(text)&nbsp; &nbsp;模糊匹配（自己含有，或后代含有）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'span:contains(\'hello\')\');</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>三、属性过滤选择器</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'[name]\');&nbsp; &nbsp;有name属性的</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\"[name=\'zhj\']\");&nbsp; &nbsp;name属性值为zhj的</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\"[value=\'male\']\");&nbsp; &nbsp;value属性值为male的</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\"[type=\'text\']\");&nbsp; type属性值为text的</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>四、可见性过滤选择器</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; :visible&nbsp; 可见元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; :hidden&nbsp; &nbsp;隐藏元素（包含隐藏域）</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>五、&nbsp; 表单选择器：</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; :text 选择所有的输入框</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; :radio&nbsp; &nbsp;选择input为radio类型的输入框</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; :checkbox</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; :checked&nbsp; 适用于单选按钮和复选按钮 （获取当前被选中的标签）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; :selected&nbsp; 适用于下拉框</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>六、each属性</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;lis.each(function(index,ele){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;console.log($(ele).html()))</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;}</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 20);
INSERT INTO `knowledge_content` VALUES (14, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>&nbsp;一、jQuery事件:</strong></span></p>\n<p style=\"text-align: left;\">事件注册的方式：</p>\n<ul style=\"text-align: left;\">\n<li style=\"text-align: left;\">基于标签：和之前JS中使用方式一致，Jquery未加干涉</li>\n<li style=\"text-align: left;\">基于编程：Jquery提供了全新的API</li>\n</ul>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(document).ready(function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'#btn_1\').click(function(event){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log($(this.val));</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //this为当前触发事件的标签的DOM对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(this.value);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; })</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; })</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 其中---Ready事件等价于onload的作用，用于保证代码的执行在页面加载之后</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(document).ready(function(){...}); ----注册方式一头部</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(function(){...})&nbsp; &nbsp; //简写&nbsp; ----注册方式二头部</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 如果没有头部，那么执行方式以及放置位置和JS中一样</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* jQuery事件的注册方式解决了注册事件只能写在HTML代码加载完毕之后的问题，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;它可以直接写在head中</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>&nbsp;二、jQuery事件函数</strong></span></p>\n<p style=\"text-align: left;\">1.鼠标事件：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* click()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* dblclick()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* mouseover()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* mousemove()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* mouseout()</p>\n<p style=\"text-align: left;\">2.键盘事件：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* keypress()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* keydown()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* keyup()</p>\n<p style=\"text-align: left;\">3.表单事件：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* change()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* focus()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;* blur()</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>三、示例</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;style&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; [name=\'user_name\']{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; background-color:red;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/style&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //方法二&nbsp; （JQuery操作）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //给标签注册事件</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //此时的$(function()就相当于window.onload</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'#inp1\').click(function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\'xixi\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(this).val(\'不要点我\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; })</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; })</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;1&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;2&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;3&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;5&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"text\" name=\"user_name\" onclick=\"f()\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"text\" id=\"inp1\" placeholder=\"inp1\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"text\" id=\"inp2\" placeholder=\"inp2\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'li\').css(\'color\',\'red\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;/*方法一 （DOM操作）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var inp1 = document.getElementById(\'inp1\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* inp1.onclick=function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;*&nbsp; &nbsp; alert(\'hehe\')</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;*/</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//keyup()事件</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(\'#inp2\').keyup(function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //console.log($(\'#inp2\').val());</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;})</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 21);
INSERT INTO `knowledge_content` VALUES (15, '<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\"><strong>一、&nbsp;jQuery DOM：</strong></span></p>\n<p style=\"text-align: left;\">增&nbsp; 删&nbsp; &nbsp;改&nbsp; &nbsp;查(选择器和过滤器)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; jQuery提供了一系列与DOM相关的方法，使得访问和操作元素和属性变得容易</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1.获取内容：</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;--- 如果想要修改内容，则修改内容直接写在括号里</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * text()&nbsp; 设置或返回所选元素的文本内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * html()&nbsp; 设置或返回所选元素的内容(包括HTML标记)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * val()&nbsp; &nbsp;设置或返回表单字段的值</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.获取属性：</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;jQuery attr()方法用于获取属性值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * attr(\"属性名\")&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //获取属性名的值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * attr(\"属性名\",\"属性值\")&nbsp; &nbsp; &nbsp; &nbsp;//设置属性值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * attr({\'属性名1\':\'属性值&lsquo;,&rsquo;属性名2&lsquo;:&rsquo;属性值&lsquo;，...})&nbsp; //一次设置多个属性值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * removeAttr(\"属性名\")&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //删除标签的属性</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * prop 和 attr 使用一致，作用一致，但prop在获取bool类型属性时(checked,selected)更友好</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>3.添加元素：</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 创建标签：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'&lt;p&gt;我是被创建的&lt;/p&gt;\')&nbsp; &nbsp;---jq标签对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * append()&nbsp; &nbsp;在被选元素最后一个位置插入内容&nbsp; ---在标签内部</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * prepend()&nbsp; 在被选元素的开头插入内容&nbsp; &nbsp; &nbsp; &nbsp; ---在标签内部</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * after()&nbsp; &nbsp; 在被选元素之后插入内容&nbsp; &nbsp; &nbsp;--不在标签内部</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * before()&nbsp; &nbsp;在被选元素之前插入内容&nbsp; &nbsp; --不在标签内部</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>4.删除元素</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * remove()&nbsp; 删除被选元素（及其子元素）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * empty()&nbsp; &nbsp;清空元素内容&nbsp; (标签还在)</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\"><strong>二、jQuery CSS类</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; CSS()方法</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;css()方法设置或返回被选元素的一个或多个样式属性</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* css(\"样式属性名\")&nbsp; &nbsp; 获取样式属性值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* css(\"样式属性名\"，\"样式属性值\")&nbsp; &nbsp;设置样式属性值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* css({\"样式属性名1\":\"样式属性值\",\"样式属性名2\":\"样式属性值\",...})&nbsp; &nbsp;设置多个样式属性</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; css补充：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 使用on()方法添加的事件处理程序适用于当前及未来的元素（比如由脚本创建的新元素）</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>三、具体代码</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;div&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;span&gt;呵呵&lt;/span&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; 选项1：&lt;input id=\"inp1\" type=\"checkbox\" value=\"0\" checked=\"checked\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; 选项2：&lt;input type=\"checkbox\" value=\"1\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/div&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;div id=\"d1\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; 嘻嘻</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/div&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;ul id=\"u1\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;1&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;2&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;3&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;4&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;5&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;input class=\"btn\" type=\"button\" value=\"点我\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; /*获取内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * console.log($(\'div:first\').text());</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * $(\'div:first\').text(\'呵呵呵哈\')&nbsp; &nbsp;//修改标签中的内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * console.log($(\'div:first\').html());</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * $(\'.btn\').val(\'就不点\');&nbsp; &nbsp; &nbsp;//修改按钮的内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; /*获取属性</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * console.log($(\"[type=\'checkbox\']:first\").prop(\'checked\'));</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * console.log($(\"[type=\'checkbox\']:first\").attr(\'value\'));</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * $(\"input:last\").attr(\'value\',\'修改属性值\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * //$(\'#inp1\').attr({\'type\':\'password\',\'name\':\'user_name\'});&nbsp; //一次设置多个属性值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * $(\'#inp1\').removeAttr(\'type\')&nbsp; &nbsp; &nbsp;//删除标签的属性</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; /*添加元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * 法一</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * var p = $(\'&lt;p&gt;我是被创建的&lt;/p&gt;\')</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * $(\'#d1\').before(p);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; * 法二：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; $(\'#d1\').before(\'&lt;p class=\"hehe\"&gt;我是被创建的&lt;/p&gt;\');</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //删除元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //$(\'#d1\').empty();&nbsp; &nbsp;//等价于 $(\'#d1\').html(\'\')</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //CSS方法</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; $(\'#d1\').css({\'color\':\'pink\',\'background-color\':\'black\'});</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //补充</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'#d1\').on(\'click\',function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\'hehe\')</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; })</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //ul 给所有的内部li加点击事件</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'#u1\').on(\'click\',\'li\',function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(this);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; })</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 22);
INSERT INTO `knowledge_content` VALUES (16, '<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;动画&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script src=\"jquery-1.11.1.min.js\"&gt;&lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script src=\"jquery.color.js\"&gt;&lt;/script&gt;&nbsp; &nbsp; &lt;!--该代码是基于上面的js代码而存在的，因此要放在下边，顺序不能乱 --&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;style&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; div{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; width:60px;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; height:60px;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; background-color:deeppink;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; position:absolute;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; top:50px;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; left:10px;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/style&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;div&gt;&lt;/div&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f()\" value=\"消失\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f1()\" value=\"消失还原\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f2()\" value=\"向上滑动\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f3()\" value=\"滑动还原\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f4()\" value=\"向下滑动\"&gt;&nbsp; &nbsp;&lt;!-- 只有当向上滑动存在时它才会起作用 --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f5()\" value=\"渐变消失\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f6()\" value=\"渐变出现\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f7()\" value=\"自定义滑动\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').hide(3000,function(){&nbsp; &nbsp;//3000表示消失需要的时间</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\'我没了\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; });</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f1(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').toggle(3000);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f2(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').slideUp(3000);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f3(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').slideToggle(3000);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f4(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').slideDown(3000);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f5(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').fadeOut(3000);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f6(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').fadeIn(3000);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; function f7(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(\'div\').animate({</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; height:\'600px\',</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; width:\'700px\',</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; position:\'absolute\',</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; top:\'1000px\',</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; left:\'1000px\',</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; backgroundColor:\'green\'</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; },8000)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 23);
INSERT INTO `knowledge_content` VALUES (17, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;jQuery与DOM的转换&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script src=\"jquery-1.11.1.min.js\"&gt;&lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;1&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;ol&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;1&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;2&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;3&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/ol&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li id=\"li1\"&gt;2&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li class=\"li2\"&gt;3&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;p&gt;我是来凑数的&lt;/p&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //jQuery --&gt; dom</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //$(\'#li1\')[0].style.color=\'red\';&nbsp; //通过下标的方式进行转换</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //$(\'#li1\').get(0).style.color=\'red\'; //通过get()的方式进行转换</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //dom&nbsp; --&gt;&nbsp; &nbsp;jQuery</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; var a = document.getElementById(\'li1\');&nbsp; &nbsp;//DOM对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; $(a).css(\'color\',\'pink\')&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//把DOM对象扔到[]里就变成了jQuery对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 24);
INSERT INTO `knowledge_content` VALUES (18, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1.math对象属性：</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* E：返回算术常量e，即自然对数的底数（约等于2.718）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* LN2:返回2的自然对数（约等于0.693）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* LN10：返回10的自然对数（约等于2.302）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* LOG2E：返回以2为底的e的对数（约等于1.414）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* LOG10E：返回以10为底的e的对数（约等于0.434）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* PI：返回圆周率（约等于3.14159）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* SQRT1_2：返回2的平方根的倒数（约等于0.707）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* SQRT2：返回2的平方根（约等于1.414）；</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.Math对象方法：</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * abs(x)：返回x的绝对值；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * acos(x)：返回x的反余弦值；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * asin(x)：返回x的反正弦值；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * atan(x)：以介于-PI/2与PI/2弧度之间的数值来返回x的反正切值；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * atan2(y,x)：返回从x轴到点(x,y)的角度（介于-PI/2与PI/2弧度之间）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * ceil(x)：对数进行上舍入；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * floor(x)：对x进行下舍入；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * cos(x)：返回数的余弦；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * exp(x)：返回Ex的指数；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * log(x):返回数的自然对数（底为e）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * max(x,y,z,...,n)：返回x,y,z,...,n中的最高值；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * min(x,y,z,...,n)：返回x,y,z,...,n中的最底值；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * pow(x,y)：返回x的y次幂；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * random()：返回0-1之间的随机数；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * round(x)：把数四舍五入为最接近的整数;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * sin(x)：返回数的正弦；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * sqrt(x)：返回数的平方根；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * tan(x)：返回角的正切；</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 25);
INSERT INTO `knowledge_content` VALUES (19, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1. js变量定义的第一种方法</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a = 10</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(a)</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2. 第二种：先定义变量再给a赋值</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * a = 20</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(a)</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>3.字符串类型</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var&nbsp; carname = \"tesla modelx\";</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(carname);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(typeof carname);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>4. 大小写转换</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var&nbsp; carname = \"tesla modelx\";</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(carname.toUpperCase());&nbsp; //小写转大写</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(carname.toLowerCase());&nbsp; //大写转小写</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>5. 分割字符串</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var&nbsp; carname = \"tesla modelx\";</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(carname.split(\' \'));&nbsp; //以空格作为分割符，分成俩个部分</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(carname)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(carname[4])&nbsp; //可以通过下标的方式输出字符串中的某个特定字符</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>6.变量命名规则</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 变量名不能使用文字作为变量</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 可以使用以下划线开头的变量</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 不可以使用数字开头的变量</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 可以使用$开头的变量</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><strong><span style=\"font-size: 14pt;\">7. 数字类型</span></strong></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * javascript不区分整数类型和浮点类型</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>8. 布尔类型</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var y = true;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(y);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(typeof y);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>9. 数组</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;*&nbsp; &nbsp; &nbsp; &mdash;&mdash;&mdash;&mdash; 是一个对象类型</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var arr = [1,2,3,4];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* //var arr = Array(1,2,3,4,5); //效果与上述类似</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(typeof arr);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(arr);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* //遍历</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* for (var i=0;i&lt;arr.length;i++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;*&nbsp; &nbsp; console.log(arr[i]);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* //数组末尾添加元素--push</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* arr.push(6,9,5);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(arr);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* //在数组末尾删除一个元素--pop&nbsp; 同时返回删除元素的值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var r = arr.pop();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(arr);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(r);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* //切片操作</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var n_a = arr.slice(0,2);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(n_a);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(arr)</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>10. Object对象&nbsp; &mdash;&mdash;内置对象</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&mdash;&mdash;自定义对象&nbsp; class</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /* 自定义对象 &mdash;&mdash;1&nbsp; &nbsp;不建议使用</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var gyh = new Object();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(gyh);&nbsp; &nbsp; &nbsp;//最初建立的是一个空对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //属性方法</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * gyh.name = \'gyh\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * gyh.age = 13;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(gyh.age);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(gyh.name);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * gyh.eat = function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp;alert(\'吃\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(gyh);&nbsp; &nbsp; //显示所建立对象的所有属性和方法</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //gyh.eat()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*自定义对象 &mdash;&mdash;2&nbsp; &nbsp; 创建更轻便，建议使用</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var dc = {\'name\':\'dc\',\'age\':14,eat:function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;*&nbsp; &nbsp; &nbsp; alert(\'吃2\')</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* }}</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(typeof dc);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(dc);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(dc.name);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(dc.eat());</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>11. 日期类型&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash; Object对象</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var date = new Date();&nbsp; &nbsp;//创建当前系统时间</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* //var date = new Date(\'2020-8-6 19:12:24\'); //也可以创建某个特定时间的日期对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(date);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(typeof date)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var year = date.getFullYear();&nbsp; //&mdash;&mdash;&mdash;&mdash;获得当前的年份</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(year);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var mon = date.getMonth()+1;&nbsp; &nbsp;//获得当前的月份 -----月份默认是从0开始的</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(mon);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var day = date.getDay();&nbsp; &nbsp;//获得当前是周几</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(day);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var date1 = date.getDate();&nbsp; &nbsp;//获得当前的日期</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(date1);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var hour = date.getHours();&nbsp; &nbsp;//获得当前的时</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(hour);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var min = date.getMinutes();&nbsp; &nbsp;//获得当前的分</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(min);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var sec = date.getSeconds();&nbsp; &nbsp;//获得当前的秒</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(sec);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(year+\'年-\'+mon+\'月-\'+date1+\'日\')&nbsp; //当前的完整日期</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* //时间戳:它是指格林威治时间1970年01月01日08时00分00秒</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* // (北京时间1970年01月01日08时00分00秒)起至至现在的总秒数</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var time = date.getTime();&nbsp; &nbsp; &nbsp;//时间戳</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* console.log(time);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>12.&nbsp; 特殊类型</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*undefined类型：未赋值的变量的默认值为undefined</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(a)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*NaN类型：not a number</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * number中的NaN</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var b = \'sjf\'/2;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(b);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(typeof b);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*空值null类型：&nbsp; ----也是一个object对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp;//用于置空变量，不是默认值，需要手动赋值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a = \'sdf\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * a = null;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(a);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(typeof a);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>13.&nbsp; &nbsp;Math对象属性</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(Math.PI);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //求最大值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(Math.max(1,2,3,4,5,614));</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //向上取整</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(Math.ceil(6.34));&nbsp; &nbsp;//7</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //向下取整</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(Math.floor(6.54));&nbsp; &nbsp;//6</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //产生0-1之间的随机数</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(Math.random());</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //log对象(底数为e)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(Math.log(100));</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 26);
INSERT INTO `knowledge_content` VALUES (20, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1. 加法运算 &mdash;&mdash;&mdash;&mdash;&nbsp; + , ++</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //任何数与NaN运算，结果都是NaN</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //任何类型的值与字符串做加法运算，结果都是字符串类型(把它们转换成字符串并拼接)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a = 1;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var b = 5;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(a+b);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a1 = 1;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var b1 = \'2\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(a1+b1);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a2 = 1;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var b2 = true;&nbsp; &nbsp;//true等价于1，false等价于0</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(a+b2);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a3 = 1</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var b3 = NaN;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var c = a3+b3;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(typeof c,c);&nbsp; //输出c的类型及其值</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.&nbsp; 比较运算符 === 、!==</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 对于 === 符号，它是先判断类型，再判断值，如果两个都相等才会是true</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 对于 == 符号，它只判断值，不判断类型</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //逻辑运算符（与：&amp;&amp;、或：||、非：！）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //赋值运算符（= 、+= 、*= 、/= 、%=）</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>3. 三元运算符（?:）</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 表达式一？表达式二：表达式三</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; 判断表达式一是否为真，为真取表达式一的值，否则取表达式二的值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var a = 5;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var b = 10;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var c;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var max = a&gt;b?a:b;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(max);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 27);
INSERT INTO `knowledge_content` VALUES (21, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1. 基本语法</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //创建函数</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * function 方法名(参数1，参数2){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;执行体；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;return &hellip;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //调用函数</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 方法名（参数1，参数2）;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2. 返回值</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * function add(a,b){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;//return a,b;&nbsp; &nbsp; //与在py中不同，逗号隔开的返回值在html中只能返回一个，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //后面的会覆盖掉前面的值，最终返回的是最后一个值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;return [a,b]&nbsp; &nbsp; &nbsp;//如果以列表的形式包起来，则会全部返回</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var n = add(4,5);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * alert(n)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;/*var guoyuhua = new Object();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * guoyuhua.name = \'guo\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * guoyuhua.get_name = function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;return guoyuhua.name;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(guoyuhua.get_name());</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>3. 匿名函数</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var fn = function(参数a,参数b){&nbsp; &nbsp; //定义匿名函数，并赋值给一个变量 --必须赋值给一个变量，否则会报错</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; 函数体；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * fn(a,b);&nbsp; &nbsp; //调用匿名函数</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 28);
INSERT INTO `knowledge_content` VALUES (22, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>一、js引入的方式</strong></span></p>\n<p style=\"text-align: left;\">HTML的 &lt;script&gt; 标签用于把javaScript代码插入HTML中；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; script标签可以出现位置：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;（一般常放在heda中）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1.在html的head</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2.body位置</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3. 同时还可以通过引入外部的js文件&lt;script src=\"文件名.js\"&gt;&lt;/script&gt;的方式使用</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 此时&lt;script&gt; 标签放在头部</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; JS中可以使用html标签，但是要加引号\'\'</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>二、示例代码</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;初识JS&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //这是单行注释</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *这是多行注释&nbsp; &nbsp; 区别于type</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *这是多行注释</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *这是多行注释</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //警告弹窗</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;alert(\'nihao\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //向开发者工具的console中输出日志 ----从网页检查中查看</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;console.log(\'hehe\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!-- 第三种类型！！！ --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script src=\"my_js.js\"&gt;&lt;/script&gt;&nbsp; &nbsp;&lt;!-- 与外部的js建立连接 --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;div&gt;hello&lt;/div&gt;</p>\n<p style=\"text-align: left;\">&lt;!-- 第二种类型2.&nbsp; &nbsp; &nbsp; &nbsp;&lt;script&gt;--&gt;</p>\n<p style=\"text-align: left;\">&lt;!--&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\'hello world\');--&gt;</p>\n<p style=\"text-align: left;\">&lt;!--&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(\'hello world\');--&gt;</p>\n<p style=\"text-align: left;\">&lt;!--&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 29);
INSERT INTO `knowledge_content` VALUES (23, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1. 说明：</strong></span></p>\n<p style=\"text-align: left;\">通过该对象，我们需要的一些参数，比如说我们需要知道此事件作用到谁身上了，</p>\n<p style=\"text-align: left;\">可以通过event的属性值target来获得到。</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.示例</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;&lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function myClick(a){&nbsp; &nbsp;//a = 接收事件对象，从onclick=\"myClick(event)\"中传来</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\"button clicked~\");</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(a);&nbsp; &nbsp;//获得event事件中&ldquo;按键&rdquo;的方法定义及内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(a.type);&nbsp; &nbsp; //事件类型</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(a.clientX);&nbsp; //事件触发X坐标</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(a.clientY);&nbsp; //事件触发Y坐标</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(a.target);&nbsp; //事件源&mdash;&mdash;触发事件的标签&nbsp; &nbsp;//获得此event事件所作用的标签(输出该标签的内容)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(a.target.value) //获取事件源的属性值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;function f1(e){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(e);&nbsp; &nbsp; &nbsp; &nbsp;//输出鼠标移动事件的内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var div = document.getElementById(\'div2\');&nbsp; &nbsp; &nbsp;//通过id获取相应的div标签（box2）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; div.innerHTML = \'y.client=\'+e.clientY+\' \'+\"x.client=\"+e.clientX+\"&lt;br&gt;\"+\'y.screen=\'+e.screenY+\' \'+\"x.screen=\"+e.screenX;&nbsp; //向div这个id所代表的块box2内写入关于box1内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;&lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &lt;input type=\"button\" onclick=\"myClick(event)\" value=\"click_me\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;&lt;div class=\"box1\" onmouseover=\"f1(event)\"&gt;&lt;/div&gt;&nbsp; &lt;!-- 鼠标移动事件的内容 --&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 30);
INSERT INTO `knowledge_content` VALUES (24, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1.事件类型</strong></span></p>\n<p style=\"text-align: left;\">事件包括：&nbsp; &nbsp;事件注册，可用事件和Event对象</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.触发方式</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;使用onclick触发JS中的f()事件</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; onclick可以加在任何一个标签中。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; body比较特殊，只有当body中有内容时，onclick点击事件才会生效</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>3.可用事件有如下</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onclick ：&nbsp; 鼠标点击时触发；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;ondblclick ： 鼠标双击时触发；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onfocus ：&nbsp; 元素获得焦点时触发；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onblur ：&nbsp; 元素失去焦点时触发；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onchange ： 用户改变域的内容；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onmouseover ： 鼠标被移动到某个元素上（触发一次）；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onmousemove ： 鼠标在元素内部移动时，不断触发；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onmouseout ： 鼠标从元素移开；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onkeydown ： 键盘某个键被按下；&nbsp; &nbsp;//第一个键被按下不会触发，只有当第二个按键按下的时候，前一个才会被触发</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onkeypress ： 键盘的键被按下或按住；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onkeyup ： 键盘的键被松开；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;onload ： 用于body标签，页面加载完毕时触发一次</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>4.事件注册的两种方法</strong></span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 12pt;\"><strong>(1)head中声明，body中调用</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<pre style=\"background-color: #2b2b2b; color: #a9b7c6; font-family: \'Consolas\'; font-size: 9.8pt;\"><br /><span style=\"color: #e8bf6a;\">&lt;html&gt;<br /></span><span style=\"color: #e8bf6a;\"><br /></span><span style=\"color: #e8bf6a;\">&lt;head&gt;<br /></span><span style=\"color: #e8bf6a;\">    &lt;script&gt;<br /></span><span style=\"color: #e8bf6a;\">         </span>function f(){<br />                alert(\"<span style=\"font-family: \'Adobe 宋体 Std L\';\">我被点击了</span>\");<br />         }<br />    <span style=\"color: #e8bf6a;\">&lt;/script&gt;<br /></span><span style=\"color: #e8bf6a;\">&lt;/head&gt;<br /></span><span style=\"color: #e8bf6a;\"><br /></span><span style=\"color: #e8bf6a;\">&lt;body&gt;<br /></span><span style=\"color: #e8bf6a;\"><br /></span><span style=\"color: #808080;\">&lt;!-- 1. --&gt;<br /></span><span style=\"color: #808080;\">  </span><span style=\"color: #e8bf6a;\">&lt;input </span><span style=\"color: #bababa;\">type</span><span style=\"color: #a5c261;\">=\"button\" </span><span style=\"color: #bababa;\">value</span><span style=\"color: #a5c261;\">=\"</span><span style=\"color: #a5c261; font-family: \'Adobe 宋体 Std L\';\">点击</span><span style=\"color: #a5c261;\">\" </span><span style=\"color: #bababa;\">onclick</span><span style=\"color: #a5c261;\">=\"f()\"</span><span style=\"color: #e8bf6a;\">&gt;  </span><span style=\"color: #808080;\">&lt;!-- input</span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\">元素被点击后会出发</span><span style=\"color: #808080;\">onclick</span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\">事件</span><span style=\"color: #808080;\">--&gt;</span><span style=\"color: #808080;\"><br /></span><span style=\"color: #e8bf6a;\"><br /></span><span style=\"color: #e8bf6a;\">&lt;/body&gt;<br /></span><span style=\"color: #e8bf6a;\">&lt;/html&gt;<br /></span></pre>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><strong style=\"font-size: 16px;\">(2)body中声明，body中调用</strong></p>\n<pre style=\"background-color: #2b2b2b; color: #a9b7c6; font-family: \'Consolas\'; font-size: 9.8pt;\"><br /><span style=\"color: #e8bf6a;\">&lt;html&gt;<br /></span><span style=\"color: #e8bf6a;\"><br /></span><span style=\"color: #e8bf6a;\">&lt;head&gt;</span><span style=\"color: #e8bf6a;\">   </span><span style=\"color: #e8bf6a;\"><br /></span><span style=\"color: #e8bf6a;\">&lt;/head&gt;<br /></span><span style=\"color: #e8bf6a;\"><br /></span><span style=\"color: #e8bf6a;\">&lt;body&gt;</span><span style=\"color: #808080;\"><br /></span><span style=\"color: #808080;\">    </span><span style=\"color: #e8bf6a;\">&lt;input </span><span style=\"color: #bababa;\">type</span><span style=\"color: #a5c261;\">=\"button\" </span><span style=\"color: #bababa;\">id</span><span style=\"color: #a5c261;\">=\"inp2\" </span><span style=\"color: #bababa;\">value</span><span style=\"color: #a5c261;\">=\"</span><span style=\"color: #a5c261; font-family: \'Adobe 宋体 Std L\';\">点我</span><span style=\"color: #a5c261;\">\"</span><span style=\"color: #e8bf6a;\">&gt;<br /></span><span style=\"color: #e8bf6a;\">    &lt;script&gt;<br /></span><span style=\"color: #e8bf6a;\">       </span>var inp2 = document.getElementById(\'inp2\');   //<span style=\"font-family: \'Adobe 宋体 Std L\';\">通过</span>id<span style=\"font-family: \'Adobe 宋体 Std L\';\">获取</span>input<span style=\"font-family: \'Adobe 宋体 Std L\';\">标签<br /></span><span style=\"font-family: \'Adobe 宋体 Std L\';\">             </span>inp2.onclick = function(){                   //<span style=\"font-family: \'Adobe 宋体 Std L\';\">用标签调用</span>onclick <span style=\"font-family: \'Adobe 宋体 Std L\';\">触发事件，并为该事件定义一个函数，   点击的时候会调用该函数<br /></span><span style=\"font-family: \'Adobe 宋体 Std L\';\">                </span>alert(\'<span style=\"font-family: \'Adobe 宋体 Std L\';\">我被点击了</span>\');<br />       }<br />     <span style=\"color: #e8bf6a;\">&lt;/script&gt;</span><span style=\"color: #e8bf6a;\"><br /></span><span style=\"color: #e8bf6a;\">&lt;/body&gt;<br /></span><span style=\"color: #e8bf6a;\">&lt;/html&gt;<br /></span></pre>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">5.示例</p>\n<pre style=\"background-color: #2b2b2b; color: #a9b7c6; font-family: \'Consolas\'; font-size: 9.8pt;\"><span style=\"color: #808080;\">  </span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\">调用函数中的</span><span style=\"color: #808080;\">this</span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\">代表当前对象</span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\"><br /></span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\">          比如</span><span style=\"color: #808080;\">onmouseover</span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\">事件被哪个元素触发了，这个</span><span style=\"color: #808080;\">this</span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\">就代表的是哪个</span><span style=\"color: #808080;\">html</span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\">中的元素<br /></span><span style=\"color: #808080; font-family: \'Adobe 宋体 Std L\';\"> </span></pre>\n<p style=\"text-align: left;\">&nbsp;</p>\n<pre style=\"background-color: #2b2b2b; color: #a9b7c6; font-family: \'Consolas\'; font-size: 9.8pt;\"><span style=\"color: #e8bf6a;\">&lt;input </span><span style=\"color: #bababa;\">type</span><span style=\"color: #a5c261;\">=\"text\" </span><span style=\"color: #bababa;\">id</span><span style=\"color: #a5c261;\">=\"inp1\" </span><span style=\"color: #bababa;\">name</span><span style=\"color: #a5c261;\">=\"user_name\" </span><span style=\"color: #bababa;\">onfocus</span><span style=\"color: #a5c261;\">=\"</span>f1(this)<span style=\"color: #a5c261;\">\" </span><span style=\"color: #bababa;\">onblur</span><span style=\"color: #a5c261;\">=\"</span>f2()<span style=\"color: #a5c261;\">\" </span><span style=\"color: #bababa;\">onchange</span><span style=\"color: #a5c261;\">=\"</span>f3()<span style=\"color: #a5c261;\">\"</span><span style=\"color: #e8bf6a;\">&gt;<br /><br /><br />&lt;script&gt;<br /></span></pre>\n<pre style=\"font-family: Consolas; font-size: 9.8pt;\">function f1(th){<br />   // th.style.color = \"red\";<br />    //alert(\'<span style=\"font-family: \'Adobe 宋体 Std L\';\">我被聚焦了</span>\');<br />    console.log(\'<span style=\"font-family: \'Adobe 宋体 Std L\';\">我被聚焦了</span>\');<br />}</pre>\n<pre style=\"background-color: #2b2b2b; color: #a9b7c6; font-family: \'Consolas\'; font-size: 9.8pt;\"><span style=\"color: #e8bf6a;\">&lt;/script&gt;<br /><br /></span></pre>\n<p style=\"text-align: left;\">&nbsp;</p>', 31);
INSERT INTO `knowledge_content` VALUES (25, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>一、说明</strong></span></p>\n<p style=\"text-align: left;\">一个标签触发事件后，事件会向上传递给自己的父标签（前提是同类事件比如onclick事件），然后父标签的父标签...</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 事件的冒泡，不会因为事件源没有该事件而不冒泡，也不会因为某个中间的父级标签没有该事件而停止冒泡。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 注意：当父元素和子元素都有某一个事件且需要独立执行时，冒泡会使得事件同时执行，所以需要防止。</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.示例</strong></span></p>\n<p style=\"text-align: left;\">&lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;div id=\"div1\" onclick=\"f1()\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"点我\" onclick=\"f(this)\"&gt;&nbsp; &nbsp;&lt;!-- 该事件触发后会传递给自己的父标签，让父标签的同样事件执行，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 如果父标签的事件同该标签的事件不是一类，那么不会进行传递</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"异类事件\" onmouseover=\"f(this)\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"阻断事件\" onclick=\"f2(event)\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;</p>\n<p style=\"text-align: left;\">&lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp;&lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(th){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(th);&nbsp; &nbsp; &nbsp; &nbsp;//获取点击事件所作用的对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(\'我是按钮\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f1(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(\'我是div\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f2(e){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; e.stopPropagation();&nbsp; &nbsp;//阻断事件向上传递</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(\'我是阻断事件\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 32);
INSERT INTO `knowledge_content` VALUES (26, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;this当前元素&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(th){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(th);&nbsp; /*获取当前的标签内容*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!-- this指代这个事件被触发时，作用在这个事件的标签--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"radio\" name=\"gender\" value=\"1\" onclick=\"f(this)\"&gt;男</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"radio\" name=\"gender\" value=\"0\" onclick=\"f(this)\"&gt;女</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"radio\" name=\"gender\" value=\"2\" onclick=\"f(this)\"&gt;其他</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"radio\" name=\"gender\" value=\"3\" onclick=\"f(this)\"&gt;保密</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 33);
INSERT INTO `knowledge_content` VALUES (27, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1. DOM（Document Object Model）:</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; HTML DOM(文档对象模型)：当网页被加载时，浏览器会创建页面的文档对象模型。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 通过HTML DOM，可以访问JavaScript HTML 文档的所有元素。</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>&nbsp;2. HTML DOM树</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 通过可编程的对象模型，JavaScript获得了足够的能力来创建动态的HTML</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * JavaScript能够改变页面中所有的HTML元素；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * JavaScript能够改变页面中所有的HTML属性；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * JavaScript能够改变页面中所有的CSS样式；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * JavaScript能够对页面中所有的事件做出反应。</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 34);
INSERT INTO `knowledge_content` VALUES (28, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1.&nbsp; 查找HTML元素</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp;通常，使用JavaScript，需要操作HTML元素。因为必须首先找到该元素。有四种方法：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; * 通过id找到HTML元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.getElementById()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; * 通过标签名找到HTML元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.getElementsByTagName()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; * 通过类名找到HTML元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.getElementsByClassName()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; * 通过name属性值找到HTML元素</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.getElementsByName()</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 35);
INSERT INTO `knowledge_content` VALUES (29, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;修改HTML元素属性&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*该功能函数是：通过点击按钮，将链接的链接地址进行更改，同时链接内容也进行更改；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 对于单选按钮选择特定的默认的选项；对多选按钮也选择特定默认的选项；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 对于下拉框也选择默认的选项；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 修改table的边框属性；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.onload = function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var btn = document.getElementById(\'btn1\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; btn.onclick = function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;// btn.value = \'别点了\';&nbsp; &nbsp; //修改标签值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;// btn.type = \'text\';&nbsp; &nbsp; &nbsp; //修改标签类型</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var a = document.getElementsByTagName(\'a\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;a.href = \'http://www.7k7k.com\'&nbsp; &nbsp; //修改链接地址</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;a.innerHTML = \'跳到7k7k\'&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //修改链接内容</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var r = document.getElementsByName(\'gender\')[1]; //获取第二个标签女</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//checked表示默认选择的标签，它的值可以是checked，可以是1，也可以是true.</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;r.previousElementSibling.checked = \'1\';&nbsp; &nbsp;//通过获取上一个兄弟标签来获得标签男</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var div = document.getElementsByTagName(\'div\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;div.children[1].checked=\'1\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;div.children[2].checked=\'1\';</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var s = div.nextElementSibling;&nbsp; &nbsp;//获取div的下一个兄弟标签select</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;s.children[2].selected=\'1\';&nbsp; &nbsp; &nbsp; //将第三个标签设为默认标签</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var t = div.nextElementSibling.nextElementSibling;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;t.border = \'100px\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"点击\" id=\"btn1\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"http://www.baidu.com\"&gt;跳到百度&lt;/a&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"radio\" value=\"1\" name=\"gender\"&gt;男</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"radio\" value=\"0\" name=\"gender\"&gt;女</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;div&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"checkbox\" name=\"hobby\"&gt;抽烟</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"checkbox\" name=\"hobby\"&gt;喝酒</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"checkbox\" name=\"hobby\"&gt;烫头</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;select name=\"s1\" id=\"\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;option value=\"\"&gt;北京&lt;/option&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;option value=\"\"&gt;上海&lt;/option&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;option value=\"\"&gt;广西&lt;/option&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/select&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;table&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/table&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 36);
INSERT INTO `knowledge_content` VALUES (30, '<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;修改HTML元素的样式&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp;注意：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;JS修改样式：属性都是小驼峰，如listStyleType ；属性值是CSS里边的值，但是要加引号</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 而HTML中是list-style-type:</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; //&nbsp; 1.&nbsp; &lt;!-- 更换背景风格 --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var ul = document.getElementsByTagName(\'ul\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ul.style.border = \'solid red 1px\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ul.style.color = \'orange\';&nbsp; &nbsp;//更换列表字体颜色</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //JS修改样式：属性都是小驼峰&nbsp; HTML中是list-style-type:</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ul.style.listStyleType = \'none\';&nbsp; &nbsp; &nbsp;//取消无序列表的项目符号类型</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //ul.style.folat=\'left\';&nbsp; &nbsp; //修改ul标签浮动位置</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for(var i=0;i&lt;ul.children.length;i++){&nbsp; &nbsp;//修改所有子标签的浮动位置</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ul.children[i].style.float=\'left\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var a = document.getElementsByTagName(\'a\')[0];&nbsp; &nbsp; //获取链接标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a.style.textDecoration=\'none\';&nbsp; &nbsp; &nbsp; //删除链接的下划线</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a.style.color=\'black\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a.style.backgroundColor=\'red\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a.style.position=\'fixed\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a.style.top=\'50%\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a.style.left=\'50%\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //&nbsp; &nbsp;2.&nbsp; &nbsp;&lt;!-- 选不同的选项显示不同的图片 --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f1(th){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(\'呵呵\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var div = document.getElementsByTagName(\'div\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(th.value===\'1\'){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //div.style.backgroundColor=\'gray\';&nbsp; &nbsp; &nbsp; &nbsp; //设置背景颜色</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; div.style.backgroundImage=\"url(\'1.jpg\')\";</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; div.style.backgroundRepeat=\'no-repeat\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; div.style.backgroundImage=\"url(\'2.jpg\')\";</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; div.style.backgroundRepeat=\'no-repeat\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!-- 更换背景风格 --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;html&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;css&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;javascript&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"\"&gt;我是一个普普通通的标a签&lt;/a&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"更换背景风格\" onclick=\"f()\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!-- 选不同的选项显示不同的图片 --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;div style=\"height:900px;width:900px\"&gt;&lt;/div&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp; &nbsp;onchange不能加在option中&nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;select name=\"\" id=\"s1\" onchange=\"f1(this)\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;option value=\"1\"&gt;动物1&lt;/option&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;option value=\"0\"&gt;动物2&lt;/option&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/select&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 37);
INSERT INTO `knowledge_content` VALUES (31, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;常用技巧&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*---------------***************常用技巧一*************-------***/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*-----阻止链接跳转的单击事件-----*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f1(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(\'我是a标签的业务逻辑代码\')</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*在点击链接的时候，实现先执行function函数，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 然后再选择跳转的地址</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f2(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for(var i=0;i&lt;1000;i++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(i);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.location.href = \'http://www.4399.com\';&nbsp; &nbsp; /*选择跳转的地址*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*----------***************常用技巧二* form表单***********-------------*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*----------模拟用户注册界面-----------*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f3(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var user_name = document.getElementById(\'inp1\').value;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var user_pwd = document.getElementById(\'inp2\').value;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(user_name,user_pwd);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(user_name==\'\'||user_pwd==\'\'){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\'用户名或者密码不能为空\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; else{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*提交*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*document.forms是一个数组对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 它表示HTML页面中所有的form标签，它的下标[0]表示第一个form标签。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; 该form标签有一个submit()方法。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*法一*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.forms[0].submit();&nbsp; &nbsp; /*手动获取form标签(同时表单的提交地址也获取到了)并调取submit()方法提交到目的地址*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;/* 法二：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* var f1 = document.getElementById(\'f1\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* f1.submit();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--***************常用技巧一*************- --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"http://www.baidu.com\" onclick=\"f1()\"&gt;点击&lt;/a&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp;为超链接注册单击事件，需要阻止其href的跳转 **(javascript:void(0))**&nbsp; &nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"javascript:void(0)\" onclick=\"f2()\"&gt;点我&lt;/a&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!----------***************常用技巧二*form表单***********-------------* --&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp;form表单提交前，如果需要处理其他逻辑：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 先将提交按钮改为普通按钮，然后在按钮事件中手动提交表单，即可实现灵活的表单控制！</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--action:提交表单的目的地址</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; method:get：显示提交，不做安全处理，可以看到提交内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 文本框要加name属性，否则百度用户信息处理模块不会接收所提交的信息</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;form id=\"f1\" action=\"http://www.baidu.com\" method=\"get\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;用户名：&lt;input id=\"inp1\" type=\"text\" name=\"user_name\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;密码：&lt;input id=\"inp2\" type=\"password\" name=\"user_pwd\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp;&lt;input type=\"submit\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;做登录注册的时候一般不用submit标签，会使用button标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;实际应用中，会先在前端处理一些逻辑，比如验证用户名和密码是否正确，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;&lt;input type=\"button\" onclick=\"f3()\" value=\"注册\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/form&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 38);
INSERT INTO `knowledge_content` VALUES (32, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;验证密码&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var inp = document.getElementsByClassName(\'inp\')[1];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var password = inp.value;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //1.当密码长度小于6位数，提示：密码长度不得低于六位</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //2.当密码长度大于6位数，但是密码是纯数字的，提示请输入数字和英文的组合</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var span = inp.nextElementSibling;&nbsp; //获取input的下一个兄弟标签span</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(password.length&lt;6){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; inp.value=\'\';&nbsp; &nbsp;// 当密码输入不符合规范时，清空密码和用户名</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; span.innerHTML=\'密码长度不得低于六位\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; span.style.color = \'red\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else if(password==Number(password)){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; span.innerHTML=\'请输入数字和英文的组合\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; span.style.color = \'orange\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; span.innerHTML=\'&radic;\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; span.style.color = \'green\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; onblur(元素失去焦点时)的时候，验证密码是否合法，并返回提示&nbsp; --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; 用户名：&lt;input class=\"inp\" type=\"text\" name=\"user_name\"&gt;&lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; 密码： &lt;input class=\"inp\" type=\"password\" name=\"user_password\" onblur=\"f()\"&gt; &lt;span&gt;&lt;/span&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 39);
INSERT INTO `knowledge_content` VALUES (33, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1. 新增标签：</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;document.createElement(标签名)&nbsp; &nbsp;创建新标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;document.createTextNode(内容)&nbsp; &nbsp; 创建标签内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;ele.appendChild(标签/内容)&nbsp; &nbsp; &nbsp; &nbsp;追加子标签/在标签内追加内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;ele.insertBefore(标签1，标签2)&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;在ele标签中，将标签1(新标签)插入到标签2(原有标签)的前面</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.示例代码</strong></span></p>\n<p style=\"text-align: left;\">&lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*该函数的功能：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; 为已有列表ul继续添加li列属性并添加内容，同时指定添加的位置</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var li1 = document.createElement(\'li\');&nbsp; &nbsp; //创建新标签li1 （只是创建，没有决定从属关系）&nbsp; 第一</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var li = document.createElement(\'li\');&nbsp; &nbsp; //创建新标签li （只是创建，没有决定从属关系）&nbsp; &nbsp; 第一</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var content = document.createTextNode(\'山西\');&nbsp; &nbsp;//在新标签中创建标签内容（只是创建，没有决定从属关系） 第二</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; li.appendChild(content);&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//&nbsp; &nbsp;第三&nbsp; &nbsp; 在标签内追加内容（决定从属关系）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var ul = document.getElementsByTagName(\'ul\')[0];&nbsp; &nbsp;//获取ul标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ul.appendChild(li);&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;// 给ul追加子标签li （决定从属关系）&nbsp; 第四 （最终决定权）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*注意&uarr;&uarr;&uarr;&uarr;&uarr;&uarr;&uarr;&uarr;：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 第三和第四有四种组合方式，但只有它们的标签一致才能输出内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 决定权由大到小：ul. &gt; li.</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var li2 = document.getElementsByTagName(\'li\')[2];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ul.insertBefore(li,li2);&nbsp; &nbsp; //在ul中，将li插入到li2的前面</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*该函数的功能：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 当点击按钮时，会自动添加input标签，同时还可以修改input的类型</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f1(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var form = document.getElementsByTagName(\'form\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var inp = document.createElement(\'input\');&nbsp; &nbsp;//创建新标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; inp.type = \'radio\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; form.appendChild(inp);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;北京&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;上海&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;广西&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;form action=\"\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"text\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/form&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"点我\" id=\"btn\" onclick=\"f()\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"实践\" id=\"btn1\" onclick=\"f1()\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 40);
INSERT INTO `knowledge_content` VALUES (34, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>1. 删除标签：</strong></span></p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;removeChild(child) 删除指定的子标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;remove()&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 删除自身</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><strong>2.示例代码</strong></span></p>\n<p style=\"text-align: left;\">&lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //修改链接样式</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.onload=function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var tr = document.getElementsByTagName(\'tr\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for(var i=1;i&lt;tr.length;i++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(tr[i].children[3]);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; tr[i].children[3].children[0].style.textDecoration=\'none\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; tr[i].children[3].children[0].style.color=\'black\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //删除行</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(t){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; t.parentNode.parentNode.remove(); //删除该行</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;table border=\"1px\" cellspacing=\"0px\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;名称&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;图片&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;价格&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;操作&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;2&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;3&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;a href=\"javascript:void(0)\" onclick=\"f(this)\"&gt;删除&lt;/a&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;4&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;5&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;6&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;a href=\"javascript:void(0)\" onclick=\"f(this)\"&gt;删除&lt;/a&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;7&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;8&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;9&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;a href=\"javascript:void(0)\" onclick=\"f(this)\"&gt;删除&lt;/a&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/table&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 41);
INSERT INTO `knowledge_content` VALUES (35, '<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(th){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //获取用户输入的值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //var inp1 = document.getElementById(\'inp1\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //console.log(inp1);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //删除标签__remove()</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var inp1 = th.previousElementSibling.previousElementSibling;&nbsp; //获取id为inp1的文本标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var value = inp1.value;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var tb = document.getElementsByTagName(\'table\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var tr = tb.children[0].children[Number(value)];&nbsp; &nbsp;//获取指定行</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; tr.remove();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f1(th){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var inp1 = th.previousElementSibling.previousElementSibling.previousElementSibling;&nbsp; //获取id为inp1的文本标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var value = inp1.value;&nbsp; &nbsp; &nbsp; &nbsp; //获取input的文本内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var tb = document.getElementsByTagName(\'table\')[0];&nbsp; &nbsp;//获取table标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var tbody = tb.children[0];&nbsp; &nbsp; &nbsp; &nbsp; //&nbsp; 获取整个表</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var tr = document.getElementsByTagName(\'tr\')[Number(value)];&nbsp; //获取下标值为value的标签tr(由input中输入的内容决定)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; tbody.removeChild(tr);&nbsp; &nbsp; //删除该tr中的内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(tbody);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;table border=\"1px\" cellspacing=\"0px\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;名称&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;图片&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;价格&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;操作&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;1&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;2&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;3&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;a href=\"javascript:void(0)\"&gt;删除&lt;/a&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;4&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;5&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;6&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;a href=\"javascript:void(0)\"&gt;删除&lt;/a&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;7&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;8&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;9&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;a href=\"javascript:void(0)\"&gt;删除&lt;/a&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/table&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; 请输入要删除的行：&lt;input type=\"text\" id=\"inp1\"&gt; &lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"确定(删除自身)\" onclick=\"f(this)\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"确定(删除指定的子标签)\" onclick=\"f1(this)\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 42);
INSERT INTO `knowledge_content` VALUES (36, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;修改标签内容&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp; 使用innerText与innerHTML属性进行修改&nbsp; &nbsp; --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var p1 = document.getElementsByTagName(\'p\')[0];</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //获取的是整个标签内部的文本内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(p1.innerText);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //获取的是标签内部全部内容，包括文本和HTML标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(p1.innerHTML);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var p = document.getElementsByTagName(\'p\')[1];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //更换标签内部的所有HTML文本</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p.innerHTML=\'分布式架构基于传统架构演变过来的。将传统的项目以项目模块进行拆分成n多个子项目。\'</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var btn = document.getElementsByTagName(\'button\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; btn.innerHTML = \'暂停\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;p&gt;随着移动终端普及和网络的提速，&lt;span&gt;短平快的大流量传播内容&lt;/span&gt;逐渐获得广大平台、&lt;/p&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;p&gt;传统架构其实就是SSH或者SSM架构，属于单点应用，把整个业务模块都会在一个项目进行开发，分为MVC架构，会拆分业务逻辑层，控制层，数据库访问层。&lt;/p&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" onclick=\"f()\" value=\"更换冷笑话\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;button&gt;播放&lt;/button&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 43);
INSERT INTO `knowledge_content` VALUES (37, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;动态表格&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //该函数的功能是改变表格的展示方法</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f1(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var t2 = document.getElementById(\'t2\');&nbsp; &nbsp; &nbsp;//获取表格主体</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //t2.hidden=true;&nbsp; &nbsp; &nbsp; &nbsp; //可以设置将表格内容不展示</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(t2.style.display===\'\'){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; t2.style.display=\'none\';&nbsp; //可以设置将表格内容不展示</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; t2.style.display=\'\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //该函数的功能是将用户输入的内容进行提交并自动向表格中添加该数据</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var count=0;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*该功能存在问题，删除数据后，会把删除操作输入文本框，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 同编号也存在问题，删除数据后，继续添加时，不能及时更新编号信息。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f2(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; count++;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var inps = document.getElementsByClassName(\'inp\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var uname = inps[0];&nbsp; //获取用户名标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var age = inps[1];&nbsp; //获取年龄标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var hobbys = document.getElementsByName(\'hobby\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var hobby = \'\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //判断多选框中内容是否被选中，如果选中则添加到hobby中</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for(var i=0;i&lt;hobbys.length;i++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(hobbys[i].checked){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; hobby+=hobbys[i].value+\' \';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //创建td</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var textid = document.createTextNode(count);&nbsp; //创建标签内容-用户名</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var td0 = document.createElement(\'td\');&nbsp; //创建新标签td</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; td0.appendChild(textid);&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //在标签td0内追加内容textid</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var textname = document.createTextNode(uname.value);&nbsp; //创建标签内容-用户名</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var td1 = document.createElement(\'td\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; td1.appendChild(textname);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var textage = document.createTextNode(age.value);&nbsp; &nbsp; &nbsp;//创建标签内容-年龄</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var td2 = document.createElement(\'td\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; td2.appendChild(textage);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var texthobby = document.createTextNode(hobby);&nbsp; &nbsp; &nbsp; &nbsp;//创建标签内容-爱好</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var td3 = document.createElement(\'td\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; td3.appendChild(texthobby);</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//** ****此用法注意**********</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*创建删除标签的方法一</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var inpdel = document.createElement(\"input\");</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * inpdel.type=\'button\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * inpdel.value=\'删除\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //该功能不完善，没有实现删除对应的行</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //且此处只能用匿名函数，不能调用其他函数</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * inpdel.onclick = function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;var trs = document.getElementsByTagName(\'table\')[0].children[1];&nbsp; &nbsp;//获取tr标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;trs.remove();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var td4 = document.createElement(\'td\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * td4.appendChild(inpdel);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //（更简便）创建删除标签的方法二：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var td4 = document.createElement(\'td\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; td4.innerHTML = \'&lt;input type=\"button\" value=\"删除\" onclick=\"f4(this)\"&gt;\';</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //创建tr</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var tr = document.createElement(\'tr\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var arr = [td0,td1,td2,td3,td4];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for(var j=0;j&lt;arr.length;j++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; tr.appendChild(arr[j]);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //将tr加入table中</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var t1 = document.getElementById(\'t1\');&nbsp; &nbsp; //获取table标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; t1.appendChild(tr);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; t1.style.textAlign = \'center\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //该功能解决了实现删除对应的行(利用的是this的定位功能)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //但是继续添加时，不能及时更新编号信息。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f4(t){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; s = t.parentNode.parentNode;&nbsp; &nbsp; //获取对应的tr行</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; s.remove();&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//删除该行</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;table width=\"400px\" id=\"t1\" border=\"2px\" cellspacing=\"0px\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;thead&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;th&gt;编号&lt;/th&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;th&gt;姓名&lt;/th&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;th&gt;年龄&lt;/th&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;th&gt;爱好&lt;/th&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;th&gt;&lt;input type=\"button\" value=\"添加\" onclick=\"f1()\"&gt;&lt;/th&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/thead&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/table&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;table id=\"t2\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;姓名&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;input type=\"text\" class=\"inp\"&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;年龄&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;input type=\"text\" class=\"inp\"&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;爱好&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"checkbox\" class=\"inp\" name=\"hobby\" value=\"吃饭\"&gt;吃饭</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"checkbox\" class=\"inp\" name=\"hobby\" value=\"睡觉\"&gt;睡觉</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"checkbox\" class=\"inp\" name=\"hobby\" value=\"打游戏\"&gt;打游戏</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;同意条款吗？&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;textarea name=\"\" id=\"\" cols=\"30\" rows=\"10\"&gt;&lt;/textarea&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;input type=\"button\" value=\"提交\" onclick=\"f2()\"&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;input type=\"reset\" value=\"重置\"&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/table&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 44);
INSERT INTO `knowledge_content` VALUES (38, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;另外的事件注册方式&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp; 解决在 HTML标签中不能出现JS代码的问题：有三种方法&nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; onload属性的含义：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 它是指在文档对象加载完成后触发(执行指定的东西)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp;document是整个HTML文档对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;window是整个浏览器对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*方法一：使用window的onload属性来帮助解决</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * 当整个浏览器加载完毕后调用function()函数</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.onload=function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var btn = document.getElementById(\'btn1\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; btn.onclick = function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\'一个普普通通的警告框\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*方法二：也可以将onload属性放到body中调用上面的函数,并且头函数变成</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * function f(){ 中间内容一样}</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input id=\"btn1\" type=\"button\" value=\"点点点\"&gt;</p>\n<p style=\"text-align: left;\">&lt;!--</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;/*方法三：写在页面加载完毕后，再为标签注册事件*/</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var btn = document.getElementById(\'btn1\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; btn.onclick = function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\'一个普普通通的警告框\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 45);
INSERT INTO `knowledge_content` VALUES (39, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;BOM操作&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; BOM（Browser Object Model）：浏览器对象模型</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 它提供了独立于内容而与浏览器窗口进行交互的对象；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 由于BOM主要用于管理窗口与窗口之间的通讯，因此其核心对象是window;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; BOM由一系列相关的对象构成，并且每个对象都提供了很对方法与属性；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp; 主要学习内容：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 与浏览器窗口交互的一些对象，例如</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 可以移动，调整浏览器大小的window对象，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 可以用于导航的location对象与history对象，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 可以获取浏览器、操作系统与用户屏幕信息的navigator与screen对象，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 可以使用document作为访问HTML文档的入口，管理框架的frames对象。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp;Window对象是JavaScript层级中的顶层对象，它代表一个浏览器窗口或一个框架&nbsp; &nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp; Document对象：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.getElement....()选择标签;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.forms[0]&nbsp; &nbsp; 选择文档中的form;&nbsp; ----------在\"/模拟用户注册_表单事件\"中</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.write[\'hello\']&nbsp; &nbsp;向文档中写入内容，不会覆盖所有之前的（了解）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; --&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //Screen对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(window.screen.width); //获取屏幕的宽</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(window.screen.height);//获取屏幕的高</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //Navigator对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(window.navigator.appVersion);&nbsp; //获得浏览器的版本</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //Document对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.write(\'hello\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.write(\'hi\');</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //Window对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; // window.alert(\'我是警号\');&nbsp; &nbsp;//1.警告框</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var a = window.confirm(\'是否删除该商品\');&nbsp; //2.确认框，根据用户的选择，返回bool值&nbsp; //自带bool值</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(a){</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else{</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f1(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var a = window.prompt(\'请输入您对该网站的意见\');&nbsp; &nbsp;//3.提示框。收集用户的输入，自带输入框</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(a);&nbsp; //自动获取输入内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //History对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f2(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.history.back();&nbsp; &nbsp;//在浏览历史离后退一步</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f3(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.history.forward(); //在浏览历史里前进一步</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //Location对象</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function f4(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(\'这是我要处理的逻辑代码\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.location.href=\'http://www.baidu.com\'&nbsp; &nbsp; //载入页面-当前载入页面的完整UR</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"javascript:void(0)\" onclick=\"f()\"&gt;删除&lt;/a&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"javascript:void(0)\" onclick=\"f1()\"&gt;提示框&lt;/a&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"返回上一页\" onclose=\"f2()\"&gt;&nbsp; &nbsp;&lt;!--//浏览器的功能（适用于两个页面之间连接成跳转功能，比如返回键）--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"button\" value=\"前进一页\" onclose=\"f3()\"&gt;&nbsp; &nbsp; &nbsp;&lt;!--//浏览器的功能--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"javascript:void(0)\" onclick=\"f4()\"&gt;touch me&lt;/a&gt;&nbsp; &nbsp;&lt;!--可以执行跳转页面的功能--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 46);
INSERT INTO `knowledge_content` VALUES (40, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;计时器&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp;var id1 = setInterval(function(){...},1000)&nbsp; 每1000ms执行一次</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var id2 = setTimeout(function(){...},1000)&nbsp; 1000ms后仅执行一次</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;clearTimeout(id2);&nbsp; &nbsp;移除事件</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;clearInterval(id1);&nbsp; 移除事件</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;--&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //第一个参数是一个函数(一般是匿名函数)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //第二个参数是一个数字，这个数字代表毫秒&nbsp; 1000ms = 1s</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //它表示每隔1000ms(1s)执行一次函数体</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //时间表</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; setInterval(function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var time = new Date();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var year = time.getFullYear();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var mon = time.getMonth();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var day = time.getDate();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var hou = time.getHours();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var min = time.getMinutes();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var sec = time.getSeconds();</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var s = document.getElementById(\'sp1\');&nbsp; //*********此时的sp1为何能加载出来？？？</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;s.innerHTML=hou+\':\'+min+\':\'+sec;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; },1000)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;span id=\"sp1\"&gt;&lt;/span&gt; &lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;span id=\"sp2\"&gt;&lt;/span&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //计时器（整个页面加载完成后才会执行）</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var t = document.getElementById(\'sp2\');&nbsp; &nbsp;//（整个页面加载完成后才会执行）见test.html</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var count = 5;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var Interval = setInterval(function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; t.innerHTML=count+\'秒后自动跳转...\';</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; count--;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(count===0){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.location.href=\'http://www.baidu.com\';&nbsp; &nbsp;//设置跳转地址</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; clearInterval(Interval);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; },1000)</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var o = setTimeout(function(){&nbsp; &nbsp; &nbsp;//3秒之后执行一次</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(\'haha\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; clearTimeout(o);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; },3000)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>', 47);
INSERT INTO `knowledge_content` VALUES (41, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;标签层级属性&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;!--&nbsp; &nbsp; &nbsp;标签层级属性：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; children:获得所有子标签&nbsp; 是一个数组。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; parentNode:获得父标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; previousElementSibling:获得上一个兄弟标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nextElementSibling:获得下一个兄弟标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; --&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;style&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; tr{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; text-align:center;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;td{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; height:50px;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; width:100px;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/style&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //功能：修改表格中的某一个td的内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; window.onload=function(){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //var tr = document.getElementsByTagName(\'tr\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //console.log(tr.parentNode.parentNode);&nbsp; &nbsp;//获取整个table标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var td1 = document.getElementById(\'td1\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(td1.nextElementSibling);&nbsp; &nbsp; &nbsp; &nbsp; //获取获得下一个兄弟标签的内容</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; td1.nextElementSibling.innerHTML=\'性取向\'&nbsp; //修改标签内容</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;1&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;2&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li id=\"i3\"&gt;3&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;4&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;5&lt;/li&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/ul&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;form action=\"\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"text\" placeholder=\"请输入\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=\"password\" placeholder=\"请输入\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/form&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*获取父标签</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var li = document.getElementById(\'i3\');</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //console.log(li.parentElement);&nbsp; &nbsp; //一般不用，会出现浏览器不匹配问题</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(li.parentNode);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var inpt = document.getElementsByTagName(\'input\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(inpt.parentNode.parentNode);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*获取所有的子标签：</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var fm = document.getElementsByTagName(\'form\')[0];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(fm.children);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;table border=\"1px\" cellspacing=\"0px\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td id=\"td1\"&gt;姓名&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;年龄&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;性别&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;学号&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;td&gt;&lt;/td&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/tr&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/table&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 48);
INSERT INTO `knowledge_content` VALUES (42, '<p style=\"text-align: left;\">&lt;!DOCTYPE html&gt;</p>\n<p style=\"text-align: left;\">&lt;html lang=\"en\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;title&gt;流程控制&lt;/title&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;script&gt;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*第一种流程控制语句</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * if (条件){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * else if(条件){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * else{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /* 简写形式max=a?b:c</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; 它表示：如果a为真，则表达式的值max为b，如果a为假，则表达式的值max为c。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*第二种流程控制语句 switch case语句</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * switch (布尔表达式或值){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;case a:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//如果值满足a,执行该语句，然后结束执行下面的语句</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;执行语句；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;break;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //如果此处没有break语句，当该条件为真且执行完毕后，</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;会继续执行下面的语句，直到遇到break，或语句全部执行完。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;case b:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//如果值满足b,执行该语句，然后结束执行下面的语句</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;执行语句；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;break;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;case c:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//如果值满足c,执行该语句，然后结束该判断语句</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;执行语句；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;break;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;default:</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;执行语句；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*for循环</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * var arr = [1,2,3,4,5];</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * for (var i=0;i&lt;arr.length;i++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;console.log(arr[i]);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*判断索引</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; var a = 5;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(a in arr);&nbsp; //判断在数组中是否存在索引a</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*九九乘法表</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * for(var i=1;i&lt;10;i++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;for(var j=1;j&lt;=i;j++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;document.write(i+\'*\'+j+\'=\'+i*j+\'&amp;nbsp;\');&nbsp; &nbsp; //将write中的内容写到html文档当中</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;}</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;document.write(\'&lt;br&gt;\')</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*while循环</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * while (循环条件){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; 执行体；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * //do&mdash;&mdash;while循环</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * do{</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp;执行体；</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }while(循环条件)</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; */</p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; /*break和continue语句</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * break：中断语句，用于终止循环语句或开关语句。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * continue：是连续语句，用于重新开始while、do wihle和for语句</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;跳过本次循环。</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * for (var i=0;i&lt;10;i++){</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * if(i==5){&nbsp; &nbsp; &nbsp; &nbsp; //对于i=5不做处理</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; *&nbsp; &nbsp; continue;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * console.log(i);</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * }</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;/script&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/head&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;body&gt;</p>\n<p style=\"text-align: left;\">&nbsp; &nbsp; &lt;/body&gt;</p>\n<p style=\"text-align: left;\">&lt;/html&gt;</p>', 49);
